import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import styles from "./DoctorManagement.module.css";

function DoctorManagement() {
  const [doctors, setDoctors] = useState([
    {
      id: 1,
      name: "Dr. Smith",
      specialization: "Cardiology",
      city: "New York",
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 2,
      name: "Dr. Jenifer",
      specialization: "Dermatology",
      city: "New York",
      image:
        "data:image/jpeg;base64,",
    },

    {
      id: 3,
      name: "Dr. Johnson",
      specialization: "Neurology",
      city: "New York",
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 4,
      name: "Dr. Sweetha",
      specialization: "Cardiology",
      city: "Los Angeles",
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 5,
      name: "Dr. Karthikeyan",
      specialization: "Dermatology",
      city: "Los Angeles",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRRtcAdqTgmM7vV8XEkpGumjp0Mcg4TsjTBPQ&s",
    },
    {
      id: 6,
      name: "Dr. Charumathi",
      specialization: "Neurology",
      city: "Los Angeles",
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 7,
      name: "Dr. Kavinkumar",
      specialization: "Cardiology",
      city: "Chicago",
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 8,
      name: "Dr. Senthur Velan",
      specialization: "Neurology",
      city: "Chicago",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcThMrrXoda7hzzuqSHntrQvLIyygC2ed7s73Q&s",
    },
    {
      id: 9,
      name: "Dr. Aadhiya",
      specialization: "Dermatology",
      city: "Chicago",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQJpvTE_s_sSmQheRF5_mmWKEw5ctkYUm6bXA&s",
    },
    {
      id: 10,
      name: "Dr. Ramakrishnan",
      specialization: "Cardiology",
      city: "New York",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRFXWbRqxogEyj7DRg86MWdAe8EZudKVKv8RA&s",
    },
    {
      id: 11,
      name: "Dr. Priya",
      specialization: "Neurology",
      city: "Chicago",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTjROdMWVj0ICCjSbFq9gVtK3nnRySzidYSYw&s",
    },
    {
      id: 12,
      name: "Dr. Srimathi",
      specialization: "Neurology",
      city: "Los Angeles",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRPCCljXZWHmGzLVU2N6kuFzNdX7tkHcnWnHg&s",
    },
  ]);

  const isAdmin = localStorage.getItem("isAdmin") === "true";
  const navigate = useNavigate(); // Use navigate for redirection

  const [newDoctor, setNewDoctor] = useState({
    name: "",
    specialization: "",
    city: "",
    image: "",
  });

  const handleRemoveDoctor = (id) => {
    const updatedDoctors = doctors.filter((doctor) => doctor.id !== id);
    setDoctors(updatedDoctors);
  };

  const handleAddDoctor = () => {
    const newDoctorToAdd = {
      id: doctors.length + 1,
      name: newDoctor.name,
      specialization: newDoctor.specialization,
      city: newDoctor.city,
      image: newDoctor.image,
    };
    setDoctors((prevDoctors) => [...prevDoctors, newDoctorToAdd]);

    // Reset the form
    setNewDoctor({ name: "", specialization: "", city: "", image: "" });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewDoctor((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Logout function to clear user data and redirect to WelcomePage
  const handleLogout = () => {
    localStorage.removeItem("isAdmin"); // Clear admin data
    navigate("/"); // Navigate to the WelcomePage
  };

  return (
    <div className={styles["doctor-management"]}>
      <h2>Choose a Doctor</h2>

      {/* Logout Button */}
      <button className={styles["logout-btn"]} onClick={handleLogout}>
        Logout
      </button>

      {/* Admin features: Add Doctor form */}
      {isAdmin && (
        <div className={styles["add-doctor-form"]}>
          <h3>Add New Doctor</h3>
          <input
            type="text"
            name="name"
            placeholder="Doctor's Name"
            value={newDoctor.name}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="specialization"
            placeholder="Specialization"
            value={newDoctor.specialization}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="city"
            placeholder="City"
            value={newDoctor.city}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="image"
            placeholder="Image URL"
            value={newDoctor.image}
            onChange={handleInputChange}
          />
          <button onClick={handleAddDoctor}>Add Doctor</button>
        </div>
      )}

      {/* Display doctors */}
      <div className={styles["doctor-container"]}>
        {doctors.length === 0 ? (
          <p className={styles["no-doctors"]}>
            No doctors available at the moment.
          </p>
        ) : (
          doctors.map((doctor) => (
            <div key={doctor.id} className={styles["doctor-item"]}>
              <img
                src={doctor.image || "https://via.placeholder.com/150"}
                alt={doctor.name}
                className={styles["doctor-image"]}
              />
              <div className={styles["doctor-info"]}>
                <h3>{doctor.name}</h3>
                <p>{doctor.specialization}</p>
                <p>{doctor.city}</p>

                {/* Allow users to book appointments */}
                {!isAdmin && (
                  <Link to={`/appointment/${doctor.id}`}>
                    <button>Book Appointment</button>
                  </Link>
                )}
              </div>

              {/* Admin features: Remove */}
              {isAdmin && (
                <div>
                  <button onClick={() => handleRemoveDoctor(doctor.id)}>
                    Remove Doctor
                  </button>
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default DoctorManagement;
